mapper.select:
SELECT TB.* FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, X.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS RNUM, a.REQUST_ID, a.OCCRRNC_DE OCCRRNC_DE, a.OCCRRNC_DATE, a.SRVC_NM, a.SVC_NM, a.METHOD_NM, a.PROCESS_SE_CODE, a.PROCESS_TIME, a.ERROR_SE, a.ERROR_CODE, a.ERROR_CO, a.RSPNS_CODE, a.RQESTER_IP, a.RQESTER_ID, a.SQL_PARAM, a.SQL_QUERY, a.METHOD_RESULT, b.ADMIN_NAME, c.PART_ID, c.PART_NM, CASE a.PROCESS_SE_CODE WHEN 'I' then '입력' WHEN 'U' then '수정' WHEN 'D' then '삭제' WHEN 'S' then '조회' ELSE '에러' END PROCESS_SE_CODE_TXT FROM COMTNSYSLOG a, TB_MANAGERINFO b, TB_PARTINFO c, (select @ROWNUM := 0) RN_TB WHERE a.RQESTER_ID = b.ADMIN_ID AND b.PART_ID = c.PART_ID
AND a.OCCRRNC_DE >= #{searchStartdday}
AND a.OCCRRNC_DE <= #{searchEndday}
AND b.ADMIN_NAME LIKE '%' || #{params.searchKeyword} || '%'
AND a.CONECT_ID LIKE '%' || #{params.searchKeyword} || '%'
AND a.PROCESS_SE_CODE = #{params.searchProcessGubun}
AND a.RQESTER_ID = #{params.searchId}
ORDER BY a.REQUST_ID DESC )X )TB ORDER BY TB.REQUST_ID DESC LIMIT #{params.firstIndex},#{params.recordCountPerPage}

mapper.insert:


		INSERT INTO COMTNSYSLOG
			( REQUST_ID
			  , SRVC_NM
			  , METHOD_NM
			  , PROCESS_SE_CODE
			  , PROCESS_TIME
			  , RQESTER_ID
			  , RQESTER_IP
			  , ERROR_CODE
			  , OCCRRNC_DE 
			  , SQLID
			  , SQL_PARAM
			  , SQL_QUERY
			  , METHOD_RESULT
			  , SVC_NM
			  , OCCRRNC_DATE
			  )
		VALUES ( #{requstId}
			  , #{srvcNm}
			  , #{methodNm}
			  , #{processSeCode}
			  , DATE_FORMAT(NOW(), '%Y%m%d%H%i%s')
			  , #{rqesterId}
			  , #{rqesterIp}
			  , #{errorCode}
			  , DATE_FORMAT(NOW(), '%Y%m%d') 
			  , #{sqlid ,jdbcType=VARCHAR}
			  , #{sqlParam, jdbcType=VARCHAR}
			  , #{sqlQuery, jdbcType=VARCHAR}
			   , #{methodResult, jdbcType=VARCHAR}
			   , 'IPCC'
			   , NOW()
        	  )
