mapper.select:
SELECT * FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS RNUM, A.OID_SEQ, A.SERVICE_SEQ, A.OID_NUMBER, A.OID_NAME, A.OID_RESULT_TYPE, A.LAST_OID_CONN_TIME, DATE_FORMAT( A.LAST_UPDT_PNTTM, '%Y-%m_%d %H:%i:%s') LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID FROM TB_SERVER_OIDINFO A, TB_SERVER_SERVICEINFO B, (select @ROWNUM := 0) RN_TB WHERE A.SERVICE_SEQ = B.SERVICE_SEQ
AND ( A.OID_NUMBER LIKE CONCAT ('%', #{params.searchKeyword},'%') OR A.OID_NAME LIKE CONCAT ('%', #{params.searchKeyword},'%') )
AND A.SERVICE_SEQ = #{params.searchServiceSeq}
) TB ) TA ORDER BY TA.RNUM DESC LIMIT #{params.firstIndex},#{params.recordCountPerPage}

mapper.insert:
INSERT INTO TB_SERVER_OIDINFO	
(
    SERVICE_SEQ, OID_NUMBER, OID_NAME, OID_RESULT_TYPE, 
	LAST_UPDT_PNTTM, LAST_UPDUSR_ID
)
VALUES 
(
	#{serviceSeq}, #{oidNumber}, #{oidName}, #{oidResultType, jdbcType=VARCHAR},
	NOW(), #{userId}
)

mapper.update:
UPDATE TB_SERVER_OIDINFO SET  OID_NUMBER = #{oidNumber}, 
							  OID_NAME = #{oidName, jdbcType=VARCHAR}, 
							  OID_RESULT_TYPE = #{oidResultType, jdbcType=VARCHAR}, 
							  LAST_UPDT_PNTTM = NOW(), 
							  LAST_UPDUSR_ID = #{userId, jdbcType=VARCHAR}
WHERE OID_SEQ = #{oidSeq}
