mapper.select:
SELECT * FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS RNUM, A.LOGIN_ID, A.NAME, A.EXTENSION, A.SN, A.SN_INDEX, A.SR, A.SR_INDEX, DATE_FORMAT( A.AGENT_UPDATE, '%Y-%m-%d') AGENT_UPDATE FROM TB_AGENTINTO A, (select @ROWNUM := 0) RN_TB WHERE 1=1
AND A.NAME LIKE CONCAT('%',#{params.searchKeyword},'%')
AND A.LOGIN_ID LIKE CONCAT('%',#{params.searchKeyword},'%')
AND A.LOGIN_ID NOT IN (SELECT PBX_LOGIN_ID FROM TB_CONSULTANT WHERE COUN_STATUS = 'Y' ) AND A.LOGIN_ID BETWEEN #{params.searchStart} AND #{params.searchEnd}
) TB ) TA ORDER BY TA.RNUM DESC LIMIT #{params.firstIndex},#{params.recordCountPerPage}

SELECT  A.LOGIN_ID, A.NAME, A.EXTENSION, A.SN, A.SN_INDEX, A.SR, A.SR_INDEX, A.AGENT_UPDATE
FROM TB_AGENTINTO A
WHERE LOGIN_ID = #{loginId}

SELECT  A.SCEN_GUBUN, A.SCEN_POSITION, A.SCEN_VALUE
FROM TB_AGENTINTO_SNSR_INFO A
WHERE LOGIN_ID = #{loginId}

insert.foreach:
INSERT INTO TB_AGENTINTO ( LOGIN_ID, NAME, AGENT_UPDATE ) VALUES ( #{item.loginId}, #{item.name}, NOW() ) ON DUPLICATE KEY UPDATE LOGIN_ID = #{item.loginId}, NAME = #{item.name}, AGENT_UPDATE = NOW(); DELETE FROM TB_AGENTINTO_SNSR_INFO WHERE LOGIN_ID = #{item.loginId};
INSERT INTO TB_AGENTINTO_SNSR_INFO(LOGIN_ID, SCEN_GUBUN, SCEN_POSITION, SCEN_VALUE) VALUES(#{item.loginId}, #{subItem.scenGubun}, #{subItem.scenPosition}, #{subItem.scenValue} )
; UPDATE TB_AGENTINTO SET SN = (SELECT SCEN_VALUE FROM TB_AGENTINTO_SNSR_INFO WHERE LOGIN_ID = #{item.loginId} AND SCEN_GUBUN = 'SN' ORDER BY SCEN_POSITION ASC LIMIT 1), SN_INDEX = (SELECT SCEN_POSITION FROM TB_AGENTINTO_SNSR_INFO WHERE LOGIN_ID = #{item.loginId} AND SCEN_GUBUN = 'SN' ORDER BY SCEN_POSITION ASC LIMIT 1), SR = (SELECT SCEN_VALUE FROM TB_AGENTINTO_SNSR_INFO WHERE LOGIN_ID = #{item.loginId} AND SCEN_GUBUN = 'SR' ORDER BY SCEN_POSITION ASC LIMIT 1), SR_INDEX = (SELECT SCEN_POSITION FROM TB_AGENTINTO_SNSR_INFO WHERE LOGIN_ID = #{item.loginId} AND SCEN_GUBUN = 'SR' ORDER BY SCEN_POSITION ASC LIMIT 1) WHERE LOGIN_ID = #{item.loginId};

mapper.insert:
INSERT INTO TB_AGENTINTO	
(
	LOGIN_ID, NAME, EXTENSION, AGENT_UPDATE
)
VALUES 
(
	#{loginId}, #{name}, #{extension,jdbcType=VARCHAR}, NOW()
)


INSERT INTO TB_AGENTINTOHISTORY(LOGIN_ID, CONSULTANT_CODE, AGT_REG_DATE, AGT_HIS_REMARK)
VALUES (#{extension}, '', NOW(), '최초 입고');

		INSERT INTO TB_AGENTINTO_SNSR_INFO(LOGIN_ID, SCEN_GUBUN, SCEN_POSITION, SCEN_VALUE)
		VALUES(#{loginId},  #{subItem.scenGubun},  #{subItem.scenPosition} ,  #{subItem.scenValue});

mapper.update:
UPDATE TB_AGENTINTO SET  NAME = #{name}, 
														EXTENSION = #{extension}, 
														AGENT_UPDATE = NOW()       
WHERE LOGIN_ID = #{loginId};
DELETE FROM TB_AGENTINTO_SNSR_INFO WHERE  LOGIN_ID = #{loginId};

		INSERT INTO TB_AGENTINTO_SNSR_INFO(LOGIN_ID, SCEN_GUBUN, SCEN_POSITION, SCEN_VALUE)
		VALUES(#{loginId},  #{subItem.scenGubun},  #{subItem.scenPosition} ,  #{subItem.scenValue});


UPDATE TB_AGENTINTO SET SN = (SELECT  SCEN_VALUE FROM TB_AGENTINTO_SNSR_INFO WHERE   LOGIN_ID = #{loginId} AND SCEN_GUBUN = 'SN' ORDER BY SCEN_POSITION ASC LIMIT 1),
				SN_INDEX = (SELECT   SCEN_POSITION FROM TB_AGENTINTO_SNSR_INFO WHERE   LOGIN_ID = #{loginId} AND SCEN_GUBUN = 'SN' ORDER BY SCEN_POSITION ASC LIMIT 1),
				SR = (SELECT SCEN_VALUE FROM TB_AGENTINTO_SNSR_INFO WHERE   LOGIN_ID = #{loginId} AND SCEN_GUBUN = 'SR' ORDER BY SCEN_POSITION ASC LIMIT 1),
				SR_INDEX = (SELECT  SCEN_POSITION FROM TB_AGENTINTO_SNSR_INFO WHERE   LOGIN_ID = #{loginId} AND SCEN_GUBUN = 'SR' ORDER BY SCEN_POSITION ASC LIMIT 1)
WHERE  LOGIN_ID = #{loginId};
