mapper.select:
SELECT * FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS RNUM, A.EXTENSION, A.TYPE, A.COR, A.COS, A.NAME, A.SECURITY_CODE, A.DISPLAY_LANGAGE, A.CONSULT_USEYN, A.TN, DATE_FORMAT(A.UPDATE_DATE, '%Y-%m-%d' ) UPDATE_DATE, A.IP_SOFTPHONE FROM TB_STATIONINFO A, (select @ROWNUM := 0) RN_TB WHERE 1=1
AND ( A.EXTENSION LIKE '%'|| #{params.searchKeyword} || '%' OR A.NAME LIKE '%' || #{params.searchKeyword} || '%' )
) TB ) TA ORDER BY TA.RNUM DESC LIMIT #{params.firstIndex},#{params.recordCountPerPage}

SELECT  A.EXTENSION, A.BUTTON_DATA, A.BUTTON_POSITION, A.BUTTON_NAME
FROM TB_STATION_BUTTIN_INFO A
WHERE EXTENSION = #{extension}

insert.foreach:
INSERT INTO TB_STATIONINFO	
(
	EXTENSION, TYPE, COR, COS, NAME, TN, SECURITY_CODE, DISPLAY_LANGAGE, CONSULT_USEYN,
	UPDATE_DATE, IP_SOFTPHONE 
)
VALUES 
(
#{item.extension}, #{item.type}, #{item.cor,jdbcType=VARCHAR}, #{item.cos,jdbcType=VARCHAR}, #{item.name,jdbcType=VARCHAR}, #{item.tn,jdbcType=VARCHAR},
#{item.securityCode}, #{item.displayLangage,jdbcType=VARCHAR}, 'N', NOW(), #{item.ipSoftphone,jdbcType=VARCHAR}
)
ON DUPLICATE KEY 
UPDATE EXTENSION =#{item.extension}, TYPE = #{item.type}, COR = #{item.cor,jdbcType=VARCHAR}, COS =  #{item.cos,jdbcType=VARCHAR},
				NAME = #{item.name,jdbcType=VARCHAR},  TN =  #{item.tn,jdbcType=VARCHAR},
				SECURITY_CODE =#{item.securityCode},  DISPLAY_LANGAGE =  #{item.displayLangage,jdbcType=VARCHAR}, 
				IP_SOFTPHONE =   #{item.ipSoftphone,jdbcType=VARCHAR}, UPDATE_DATE = NOW();
DELETE FROM TB_STATION_BUTTIN_INFO WHERE  EXTENSION = #{item.extension};

	INSERT INTO TB_STATION_BUTTIN_INFO(EXTENSION, BUTTON_DATA, BUTTON_POSITION, BUTTON_NAME)
	VALUES ( #{item.extension}, #{subItem.buttonData}, #{subItem.buttonPosition} , #{subItem.buttonName})

;

mapper.insert:
INSERT INTO TB_STATIONINFO ( EXTENSION, TYPE, COR, COS, NAME, TN, SECURITY_CODE, DISPLAY_LANGAGE, CONSULT_USEYN, UPDATE_DATE, IP_SOFTPHONE ) VALUES ( #{extension}, #{type}, #{cor,jdbcType=VARCHAR}, #{cos,jdbcType=VARCHAR}, #{name,jdbcType=VARCHAR}, #{tn,jdbcType=VARCHAR}, #{securityCode}, #{displayLangage,jdbcType=VARCHAR}, 'N', NOW(), #{ipSoftphone,jdbcType=VARCHAR} ) ON DUPLICATE KEY UPDATE EXTENSION =#{extension}, TYPE = #{type}, COR = #{cor,jdbcType=VARCHAR}, COS = #{cos,jdbcType=VARCHAR}, NAME = #{name,jdbcType=VARCHAR}, TN = #{tn,jdbcType=VARCHAR}, SECURITY_CODE =#{securityCode}, DISPLAY_LANGAGE = #{displayLangage,jdbcType=VARCHAR}, IP_SOFTPHONE = #{ipSoftphone,jdbcType=VARCHAR}, UPDATE_DATE = NOW();
DELETE FROM TB_STATION_BUTTIN_INFO WHERE EXTENSION = #{extension};
INSERT INTO TB_STATION_BUTTIN_INFO(EXTENSION, BUTTON_DATA, BUTTON_POSITION, BUTTON_NAME) VALUES ( #{extension}, #{subItem.buttonData}, #{subItem.buttonPosition}, #{subItem.buttonName})

mapper.update:
UPDATE TB_STATIONINFO SET  CONSULT_USEYN = #{consultUseyn}
															UPDATE_DATE = NOW()       
WHERE EXTENSION = #{extension}
