mapper.select:
SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM ( SELECT QUEUE_NM, QUEUE_DURATION, QUEUE_AUTODELETE, LAST_UPDUSR_ID, DATE_FORMAT(LAST_UPDT_PNTTM, '%Y-%m-%d' ) LAST_UPDT_PNTTM FROM TB_QUEUE_INFO WHERE 1=1
AND QUEUE_NM LIKE CONCAT('%',#{params.searchKeyword},'%')
ORDER BY LAST_UPDT_PNTTM DESC ) TB ORDER BY TB.LAST_UPDT_PNTTM DESC LIMIT #{params.firstIndex}, #{params.recordCountPerPage}

SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM ( SELECT EXCHANGE_NAME, QUEUE_NM, EXCHANGE_ROUTING_KEY, EXCHANGE_TYPE, LAST_UPDUSR_ID, DATE_FORMAT(LAST_UPDT_PNTTM, '%Y-%m-%d' ) LAST_UPDT_PNTTM FROM TB_EXCHANGE_INFO WHERE 1=1
AND QUEUE_NM LIKE CONCAT('%',#{params.searchKeyword},'%')
AND EXCHANGE_NAME LIKE CONCAT('%',#{params.searchKeyword},'%')
AND QUEUE_NM = #{queueNm}
ORDER BY LAST_UPDT_PNTTM DESC ) TB ORDER BY TB.LAST_UPDT_PNTTM DESC LIMIT #{params.firstIndex}, #{params.recordCountPerPage}

SELECT QUEUE_NM, QUEUE_DURATION, QUEUE_AUTODELETE, LAST_UPDUSR_ID, LAST_UPDT_PNTTM
FROM TB_QUEUE_INFO
WHERE QUEUE_NM = #{queueNm}

SELECT EXCHANGE_NAME, QUEUE_NM, EXCHANGE_ROUTING_KEY, EXCHANGE_TYPE, LAST_UPDUSR_ID, LAST_UPDT_PNTTM
FROM TB_EXCHANGE_INFO
WHERE EXCHANGE_NAME = #{exchangeName}
