mapper.select:
SELECT * FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS RNUM, A.SYSTEM_CODE, A.SYSTEM_NAME, A.SYSTEM_DC, A.SYSTEM_CREATEDC, A.DOMAIN_INFO, A.SYSTEM_USEYN, A.SYSTEM_ICON, A.SYSTEM_MENUGUBUN, A.SYSTEM_HEALTH_CHECK, A.SYSTEM_GUBUN, fn_DetailCodeNm(A.SYSTEM_GUBUN) SYSTEM_GUBUN_TXT, fn_DetailCodeNm( A.SYSTEM_HEALTH_CHECK) SYSTEM_HEALTH_CHECK_TXT, fn_DetailCodeNm( A.SYSTEM_MENUGUBUN) SYSTEM_MENUGUBUN_TXT, A.SYSTEM_HEALTH_CHECK_URL, A.SYSTEM_CONN_LEVEL, fn_DetailCodeNm( A.SYSTEM_CONN_LEVEL) SYSTEM_CONN_LEVEL_TXT FROM TB_SYSTEMINFO A, (select @ROWNUM := 0) RN_TB WHERE 1=1
AND ( A.SYSTEM_CODE LIKE CONCAT ('%', #{params.searchKeyword},'%') OR A.SYSTEM_NAME LIKE CONCAT ('%', #{params.searchKeyword},'%') )
AND A.SYSTEM_GUBUN = #{params.searchSystemGubun}
) TB ) TA ORDER BY TA.RNUM DESC LIMIT #{params.firstIndex},#{params.recordCountPerPage}

SELECT A.SYSTEM_CODE, A.SYSTEM_NAME
FROM TB_SYSTEMINFO A
WHERE 1=1 

      AND A.SYSTEM_USEYN = #{params.systemUseyn}


      AND A.SYSTEM_MENUGUBUN = #{params.systemMenuGubun}



		AND (A.SYSTEM_MENUGUBUN = 'MENU_GUBUN_1' OR A.SYSTEM_MENUGUBUN = 'MENU_GUBUN_2')


		AND A.SYSTEM_MENUGUBUN = 'MENU_GUBUN_3' 



      AND A.SYSTEM_CONN_LEVEL = #{params.systemConnLevel}

SELECT IFNULL(COUNT(*), 0)
FROM TB_SYSTEMINFO 
WHERE SYSTEM_CODE = #{systemCode}

mapper.insert:
INSERT INTO TB_SYSTEMINFO	
(
    SYSTEM_CODE, SYSTEM_NAME, SYSTEM_DC, SYSTEM_CREATEDC, DOMAIN_INFO ,SYSTEM_USEYN, SYSTEM_GUBUN, 
    SYSTEM_ICON, SYSTEM_MENUGUBUN, SYSTEM_HEALTH_CHECK, SYSTEM_HEALTH_CHECK_URL, SYSTEM_CONN_LEVEL
)
VALUES 
(
	#{systemCode}, #{systemName}, #{systemDc,jdbcType=VARCHAR}, NOW(), #{domainInfo,jdbcType=VARCHAR}
	, #{systemUseyn,jdbcType=VARCHAR}, #{systemGubun} 
	, #{systemIcon,jdbcType=VARCHAR}, #{systemMenugubun,jdbcType=VARCHAR}, #{systemHealthCheck,jdbcType=VARCHAR}
	, #{systemHealthCheckUrl,jdbcType=VARCHAR} , #{systemConnLevel,jdbcType=VARCHAR}
)

mapper.update:
UPDATE TB_SYSTEMINFO SET SYSTEM_NAME = #{systemName}, DOMAIN_INFO = #{domainInfo,jdbcType=VARCHAR}, SYSTEM_DC = #{systemDc, jdbcType=VARCHAR} , SYSTEM_USEYN =#{systemUseyn,jdbcType=VARCHAR} ,
SYSTEM_ICON = #{systemIcon, jdbcType=VARCHAR} ,
SYSTEM_MENUGUBUN = #{systemMenugubun, jdbcType=VARCHAR} , SYSTEM_HEALTH_CHECK = #{systemHealthCheck, jdbcType=VARCHAR} , SYSTEM_HEALTH_CHECK_URL = #{systemHealthCheckUrl, jdbcType=VARCHAR} , SYSTEM_CONN_LEVEL= #{systemConnLevel, jdbcType=VARCHAR} , SYSTEM_GUBUN = #{systemGubun} WHERE SYSTEM_CODE = #{systemCode}
