mapper.select:
SELECT * FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS RNUM, A.SYSTEM_CODE, A.SYSTEM_NAME, A.SYSTEM_DC, A.SYSTEM_CREATEDC, A.DOMAIN_INFO, A.SYSTEM_USEYN FROM TB_SYSTEMINFO A, (select @ROWNUM := 0) RN_TB WHERE 1=1
AND ( A.SYSTEM_CODE LIKE '%' #{params.searchKeyword} '%' OR A.SYSTEM_NAME LIKE '%' #{params.searchKeyword} '%' )
) TB ) TA ORDER BY TA.RNUM DESC LIMIT #{params.firstIndex},#{params.recordCountPerPage}

SELECT A.SYSTEM_CODE, A.SYSTEM_NAME, A.SYSTEM_DC, A.SYSTEM_CREATEDC, A.DOMAIN_INFO, A.SYSTEM_USEYN
FROM TB_SYSTEMINFO A
WHERE A.PROGRM_FILE_NM = #{progrmFileNm}

mapper.insert:
INSERT INTO TB_SYSTEMINFO	
(
    SYSTEM_CODE, SYSTEM_NAME, SYSTEM_DC, SYSTEM_CREATEDC, DOMAIN_INFO ,SYSTEM_USEYN
)
VALUES 
(
	#{systemCode}, #{systemName}, #{systemDc,jdbcType=VARCHAR}, NOW(), #{domainInfo,jdbcType=VARCHAR}
	, #{systemUseyn,jdbcType=VARCHAR}
)

mapper.update:
		UPDATE TB_SYSTEMINFO SET SYSTEM_NAME = #{systemName,jdbcType=VARCHAR}, 
								 DOMAIN_INFO = #{domainInfo,jdbcType=VARCHAR}, 
		                         SYSTEM_DC = #{systemDc, jdbcType=VARCHAR}  ,
		                         SYSTEM_USEYN =#{systemUseyn,jdbcType=VARCHAR} 
       	WHERE SYSTEM_CODE = #{systemCode}
