mapper.select:
SELECT * FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS RNUM, A.SYSTEM_CODE, A.SYSTEM_NAME, A.SYSTEM_DC, A.SYSTEM_CREATEDC, A.DOMAIN_INFO, A.SYSTEM_USEYN, A.SYSTEM_GUBUN, fn_DetailCodeNm( A.SYSTEM_GUBUN) SYSTEM_GUBUN_TXT FROM TB_SYSTEMINFO A, (select @ROWNUM := 0) RN_TB WHERE 1=1
AND ( A.SYSTEM_CODE LIKE CONCAT ('%', #{params.searchKeyword},'%') OR A.SYSTEM_NAME LIKE CONCAT ('%', #{params.searchKeyword},'%') )
AND A.SYSTEM_GUBUN = #{params.searchSystemGubun}
) TB ) TA ORDER BY TA.RNUM DESC LIMIT #{params.firstIndex},#{params.recordCountPerPage}

mapper.insert:
INSERT INTO TB_SYSTEMINFO	
(
    SYSTEM_CODE, SYSTEM_NAME, SYSTEM_DC, SYSTEM_CREATEDC, DOMAIN_INFO ,SYSTEM_USEYN, SYSTEM_GUBUN
)
VALUES 
(
	#{systemCode}, #{systemName}, #{systemDc,jdbcType=VARCHAR}, NOW(), #{domainInfo,jdbcType=VARCHAR}
	, #{systemUseyn,jdbcType=VARCHAR}, #{systemGubun} 
)

mapper.update:
		UPDATE TB_SYSTEMINFO SET SYSTEM_NAME = #{systemName}, 
								 DOMAIN_INFO = #{domainInfo,jdbcType=VARCHAR}, 
		                         SYSTEM_DC = #{systemDc, jdbcType=VARCHAR}  ,
		                         SYSTEM_USEYN =#{systemUseyn,jdbcType=VARCHAR} ,
		                         SYSTEM_GUBUN = #{systemGubun}
       	WHERE SYSTEM_CODE = #{systemCode}
