mapper.select:
SELECT * FROM ( SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM ( SELECT @ROWNUM := @ROWNUM + 1 AS RNUM, A.VOC_SEQ, A.VOC_PROCESS_SEQ, A.VOC_PROCESS_USERID, A.VOC_PROCESS_VISITED_DAY, A.VOC_PROCESS_VISITED_TIME, A.VOC_PROCESS_END_DATE, A.VOC_PROCESS, A.PRODUCT_CODE, A.FRST_REGIST_PNTTM, A.FRST_REGISTER_ID, A.LAST_UPDT_PNTTM, A.LAST_UPDUSR_ID, A.VOC_PROCESS_DC FROM TB_VOC_PROCESSINFO A, (select @ROWNUM := 0) RN_TB WHERE A.VOC_SEQ = #{params.vocSeq}
AND ( A.SERVER_NAME LIKE CONCAT ('%', #{params.searchKeyword},'%') OR A.SERVER_DC LIKE CONCAT ('%', #{params.searchKeyword},'%') )
) TB ) TA ORDER BY TA.RNUM DESC LIMIT #{params.firstIndex},#{params.recordCountPerPage}
