mapper.select:
SELECT SUM(1) OVER(PARTITION BY NULL) AS TOTAL_RECORD_COUNT, TB.* FROM ( SELECT a.ADMIN_ID , a.ADMIN_NAME , a.ADMIN_TEL , a.ADMIN_EMAIL , a.PART_ID as "partId", case a.LOCK_YN when 'Y' then 'O' else 'X' end as lockYn, a.Use_YN, DATE_FORMAT(a.REG_DATE, '%Y-%m-%d') as regDate, a.PART_ID, a.PASSWORD_HINT, a.PASSWORD_CNSR, a.ROLE_ID, b.ROLE_NAME, c.PART_NM, a.PBX_EXTENSION, a.CONSULTANT_USEYN , a.LAST_UPDT_PNTTM, a.INSTT_CODE , d.ALL_INSTT_NM FROM TB_MANAGERINFO a, TB_ROLEINFO b, TB_PARTINFO c, COMTNINSTTCODE d WHERE a.ROLE_ID = b.ROLE_ID AND a.PART_ID = c.PART_ID AND a.INSTT_CODE = d.INSTT_CODE
and a.ADMIN_ID LIKE CONCAT('%',#{params.searchKeyword},'%')
and a.ADMIN_EMAIL LIKE CONCAT('%',#{params.searchKeyword},'%')
and a.ADMIN_NAME LIKE CONCAT('%',#{params.searchKeyword},'%')
and a.USE_YN = #{params.useYn}
and a.ROLE_ID = #{params.searchRoleId}
and a.PART_ID = #{params.searchPartId}
ORDER BY a.REG_DATE DESC ) TB limit #{params.firstIndex} , #{params.recordCountPerPage}

SELECT  IFNULL(COUNT(*),0)      
FROM   TB_MANAGERINFO
WHERE  ADMIN_ID = #{adminId}

SELECT  IFNULL(COUNT(*),0)      
FROM   TB_MANAGERINFO
WHERE  ADMIN_ID = #{params.adminId}
		 AND ADMIN_PASSWORD = #{params.adminPasswordEnc}

mapper.update:
UPDATE TB_MANAGERINFO SET ADMIN_NAME = #{adminName}
                          , USE_YN = #{useYn}
                          , PART_ID = #{partId }
                          , ADMIN_TEL = #{adminTel, jdbcType=VARCHAR }
                          , PASSWORD_HINT = #{passwordHint, jdbcType=VARCHAR }
                          , PASSWORD_CNSR = #{passwordCnsr, jdbcType=VARCHAR }
                          , INSTT_CODE = #{insttCode ,jdbcType=VARCHAR}
                          , ROLE_GUBUN = #{roleGubun ,jdbcType=VARCHAR} 

                          , ADMIN_PASSWORD = #{adminPassword}
                          , UPDATE_PASSWORD = now()
                          , LOCK_YN ='N'
                          , ADMIN_STATUS = #{adminStatus ,jdbcType=VARCHAR}


                          ,  EMP_NO = #{empNo, jdbcType=VARCHAR }

                          , LAST_UPDUSR_ID = #{userId}
                          , ROLE_ID = #{roleId ,jdbcType=VARCHAR}
                          , PBX_EXTENSION = #{pbxExtension ,jdbcType=VARCHAR}
                          , CONSULTANT_USEYN = #{consultantUseyn ,jdbcType=VARCHAR}
                          , LAST_UPDT_PNTTM = NOW()
WHERE ADMIN_ID = #{adminId}
